#include <iostream>
using namespace std;

class Node
{
public:
    int data;
    Node* next;
    Node(int da, Node* p = nullptr)
    {
        this->data =  da;
        this->next = p;
    }
};

Node* reverseLinkedList(Node *head)
{
    if(head == nullptr || head->next == nullptr)
        return head;
    Node *p1 = head;
    Node *p2 = head->next;
    Node *p3 = nullptr;
    while(p2 != nullptr)
    {
        p3 = p2->next;
        p2->next = p1;
        p1 = p2;
        p2 = p3;
    }
    head->next = nullptr;
    head = p1;
    return head;
}

int main()
{
    // 
    Node *head;
    head = new Node(3);
    head->next = new Node(5);
    Node *tmp = head->next;
    tmp->next = new Node(1);
    tmp = tmp->next;
