        int num = 0;
        for(auto ch : S)
        {
            int x;
            if(isupper(ch))
            {
                x = ch - 'A';
                if(a_upper[x])  ++num;
            }
            else
            {
                x = ch - 'a';
                if(a_lower[x])  ++num;
            } 
