    //int
    QString control::decInt2HexStr(int dec)
    {
        //
        if(dec > 65535)
        {
            QMessageBox::information(NULL, "", "input < 65535");
            return "0";
        }
        QString hexStr;
        int temp = dec / 16;
        int left = dec % 16;
        if(temp > 0)
            hexStr += decInt2HexStr(temp);
        if(left < 10)
            hexStr += (left + '0');
        else
