 *        int val;
 *        struct ListNode *next;
 *        ListNode(int x) :
 *              val(x), next(nullptr) {
 *        }
 *  };
 */

// 1:
class Solution {
public:
    vector<int> printListFromTailToHead(ListNode* head) {
        vector<int> ans;
        stack<int> nodes;
        ListNode* pNode = head;
        while(pNode != nullptr)
        {
            nodes.push(pNode->val);
            pNode = pNode->next;
        }
        while(!nodes.empty())
        {
            ans.push_back(nodes.top());
            nodes.pop();
        }
        return ans;
    }
};
//2:
class Solution {
public:
    vector<int> printListFromTailToHead(ListNode* head) {
        vector<int> ans;
        ListNode* pNode = head;
        while(pNode != nullptr)
        {
            ans.insert(ans.begin(), pNode->val);
            pNode = pNode->next;
        }
        return ans;
    }
};
```

