```cpp
class Solution {
public:
	void replaceSpace(char *str,int length) {
        if(str == nullptr || length == 0)
            return;
        // 
        int numberOfBlank = 0;
        for(int i = 0; i < length; ++i)
        {
            if(str[i] == ' ')
                ++numberOfBlank;
        }
        int indexOriginal = length - 1;
        int indexNew = length + 2 * numberOfBlank - 1;
        for(int i = indexOriginal; i >= 0; --i)
        {
            if(str[i] != ' ')
            {
                str[indexNew] = str[i];
                --indexNew;
            }
            else
            {
                str[indexNew--] = '0';
                str[indexNew--] = '2';
                str[indexNew--] = '%';
            }
        }
	}
};
```

