class Solution {
public:
    int jumpFloorII(int number) {
        if(number <= 0)
            return 0;
        if(number == 1)
            return 1;
        int ans = 1;
        while(number > 0)
        {
            ans += jumpFloorII(number -1);
            --number;
        }
        return ans;
