```cpp
// 1
class Solution {
public:
    double Power(double base, int exponent) {
        double res = 1.0;
        if(exponent == 0)
            return 1;
        if(exponent < 0)
        {
            if(base == 0)
                throw "The denominator cannot be 0";
            else
                base = 1.0 / base;
        }
        while(exponent)
        {
            res *= base;
            exponent = (exponent > 0) ? --exponent : ++exponent;
        }
        return res;
    }
};
// 2:
class Solution {
public:
    double Power(double base, int exponent) {
        double res = 1.0;
        int flag = 1; // 1
        if(exponent == 0)
            return 1;
        if(exponent < 0)
        {
            if(base == 0)
                throw "The denominator cannot be 0";
            else
                exponent = -exponent;
            flag = 0;
        }
        while(exponent != 0)
        {
            if((exponent & 1) == 1)
                res *= base;
            base *= base;
            exponent >>= 1;
        }

        return flag ? res : (1 / res);
    }
};
```

