class Solution {
public:
    int StrToInt(string str) {
        int len = str.size();
        // 
        if(len == 0)
            return 0;
        int begin = 0, isNegative = 0;
        // '+', '-'
        if(str[0] == '+')
        {
            isNegative = 0;
            begin = 1;
        }
        else if(str[0] == '-')
        {
            isNegative = 1;
            begin = 1;
        }
        int position = 1;
        // long long 
        long long res = 0;
        for(int i = len -1; i >= begin; --i)
        {
            if(str[i] < '0' || str[i] > '9')
                return 0;
            res += (str[i] - '0') * position;
            position *= 10;
        }
        // 
        if((isNegative == 0 && res > 0x7fffffff) || (isNegative == 1 && res > 0x80000000))
            return 0;
        else
            res = isNegative ? -res : res;
        return res;
    }
};
```

